mapper.insert:
INSERT INTO FILE_STUDY_TABLE(
	FILE_SEQ
	,REAL_NAME
	,SAVE_NAME
	,REG_DATE
	,SAVE_PATH
	,LIST_SEQ
)VALUES(
	(select nvl(max(file_seq),0)+1 from FILE_STUDY_TABLE)
	,#{realFile}
	,#{saveFile}
	,sysdate
	,#{filePath}
	,#{listSeq}
)

mapper.update:
UPDATE BOARD_STUDY
   SET MEM_ID = #{memId}
	,MEM_NAME = #{memName}
	,BOARD_SUBJECT = #{boardSubject}
	,BOARD_CONTENT = #{boardContent}
	,UPT_DATE = sysdate
 WHERE SEQ = #{seq}

mapper.delete:
		DELETE 
			FROM BOARD_STUDY
		WHERE SEQ IN (

     		 #{seq}

    		)
